mapper.insert:

 	SELECT MP_BOARD_SEQ.NEXTVAL FROM DUAL

INSERT INTO board(    bno 
					      , title
					      , content 
					      , writer
					      , regdate
					      , viewcnt  )
              VALUES(    #{bno} 
              		  , #{title}
              		  , #{content}
              		  , #{writer}
              		  , #{regdate}
              		  , #{viewcnt}  )

mapper.update:
UPDATE board
   SET title    =   #{title},
   	   content  =   #{content}
 WHERE BNO = #{bno}
