mapper.insert:
 INSERT INTO board_member(     userid 
					       		, userpass 
					      		, username 	)
               		VALUES(   #{userid} 
              		   		, #{userpass}
              		 		, #{username})

mapper.select:
SELECT userid, userpass 
  FROM board_member
 WHERE userid = #{userid}
   AND userpass = #{userpass}

mapper.update:
UPDATE board_member SET 
userpass = #{userpass},
username = #{username}
WHERE userid = #{userid}

mapper.delete:
DELETE FROM board_member
WHERE userid = #{userid}
  AND userpass = #{userpass}

mapper.select:
SELECT COUNT(*) 
FROM board_member
WHERE userid = #{userid}
  AND userpass = #{userpass}
